void stygmorgan::PonSolo(int mcanal) {
  int i;

for (i=0; i<=4; i++)
{ 
  if ((rmgmo->CM[i].solo) && (mcanal != i ))
  
       { 
         rmgmo->CM[mcanal].solo = 0;
         ActuaSolo();
         return;
       }
       
}

for (i=9; i<=15; i++)
{ 
  if ((rmgmo->CM[i].solo) && (mcanal != i ))
  
       { 
         rmgmo->CM[mcanal].solo = 0;
         ActuaSolo();
         return;
       }
       
}

 rmgmo->AyudaSolo(mcanal);
   
   MoreSolo();
}

