void stygmorgan::semabplay() {
  if ( rmgmo->hay )
            {
              rmgmo->hay = 0;
              ponmix();
              VName->label(rmgmo->nStyle.Pattern[rmgmo->Variacion].Name);
              if (( rmgmo->tbotvar[rmgmo->Variacion] >=12) && (rmgmo->tbotvar[rmgmo->Variacion] <=14)) rmgmo->siguiente=0;
              rmgmo->sbot[rmgmo->tbotvar[rmgmo->ultima]]=0;
              rmgmo->sbot[rmgmo->tbotvar[rmgmo->Variacion]]=1;
              ActuaBoton();


            }

           if (rmgmo->endi)
            {
              rmgmo->endi=0;
              STST->value(0);
              apaga();
            }

           if (rmgmo->sic)
             {
              DCompas->label(rmgmo->dcompas);
              rmgmo->sic=0;
              
             }
             
           if (rmgmo->negra != rmgmo->onegra)
           { 
             
             rmgmo->onegra=rmgmo->negra;
             
             switch(rmgmo->onegra)
              {

           case 1:
              N1->setonly();
              break;
           case 2:
              N2->setonly();
              break;
           case 3:
              N3->setonly();
              break;
           case 4:
              N4->setonly();
              break;

              }
           }
           
          if (rmgmo->semi != rmgmo->osemi)
          { 
            rmgmo->osemi=rmgmo->semi;
            FunciLeds(0);
          }
}

void stygmorgan::sema() {
  if (rmgmo->CambiaMidi)
             {
               rmgmo->CambiaMidi=0;
               ActuaMidDevices();
               rmgmo->Conecta();
             }  
            
            
 if ((rmgmo->weventeditor==1) && (rmgmo->genmidi==0) &&(rmgmo->Pendientes > 0))
    {
     time_t ahora;
      int k=rmgmo->Pendientes;
   
     for (int i=0; i<k; i++)
          {
           ahora=time(NULL); 
           if ((rmgmo->PO[i].estado==1) &&  ((ahora - rmgmo->PO[i].delta) > 2))
              {
               rmgmo->PO[i].estado=0;
               rmgmo->envionota(rmgmo->PO[i].canal,rmgmo->PO[i].nota,0);
               rmgmo->ActuPen(); 
               }  
           }
          
     }     
          
                    
            
            
            if (rmgmo->ponvum)
             {
             rmgmo->ponvum=0;
             DMidi->value(rmgmo->vum);
             }

            if (pera == 2 )
             {
               pera=0;
               ImportSty->activate();
               ListaStyles->activate();
               Botones->activate();
               SelectType->activate();
               STST->activate();
               BroReg->activate();
               if (rmgmo->bcancel==2) ListaStyles->do_callback();
               if (rmgmo->bcancel==0) SaveStylesFunction(); else rmgmo->bcancel=0;
             }


             if (rmgmo->cc)
             {
             VChord->label(rmgmo->NombreAcorde);
             rmgmo->cc=0;
             }

             if (rmgmo->ponstart)
             {
               rmgmo->ponstart=0;
               rmgmo->bplay=1;
               STST->value(1);
               STST->do_callback();
             }
             
             
            if (rmgmo->cambioc)
           {
                 rmgmo->cambioc=0;

                if (programa==0)
                     {
                       if (rmgmo->sel) Sst->setonly(); else   Sso->setonly();
                       ActuaDsel();
                     }
                 else
                     {
                      switch(rmgmo->sel)
                          {
                            case 0:

                              if (programa <= rmgmo->numsounds)

                                   {
                                     ListaSounds->value(programa);
                                     ListaSounds->do_callback();
                                  }
                               break;
                             case 1:

                               if ((programa < 16 ) && (rmgmo->tvb[programa] != 0)) CVari(programa,0);
                               break;

                           }

                       }

             }
             
    
    
   if  (rmgmo->tapaga==1) 
   { rmgmo->tapaga=0;
     FunciLeds(1);
   }
}

