void stygmorgan::AcutaLeds(int ifun) {
  Fl_Group * Elg=VumCanal9;

int i;
int lacosa=0;
int lapapa=0;

for(i=9; i<=15;i++)
{
  if (ifun==1)
  {
   lacosa=0;
   lapapa=1;
   }
    else 
   {
    lacosa=rmgmo->lastvelo[i];
    lapapa=rmgmo->cocas[i];
   }  

switch(i)
   { 
      
      case 10:
            Elg=VumCanal10;
            break;
      case 11:
            Elg=VumCanal11;
            break;
      case 12:
            Elg=VumCanal12;
            break;
      case 13:
            Elg=VumCanal13;
            break;                        
      case 14:
            Elg=VumCanal14;
            break;
      case 15:
            Elg=VumCanal15;
            break;      
    }        

for ( int t=0; t<Elg->children(); t++ ) 
  {          
    Fl_Widget *w = Elg->child(t);
    
    if (((t+1)*127/7.5) >= lacosa)
      {
           switch(t)
              {
               case 5:
               w->color(82);
               break;
               case 6:
               w->color(80);
               break; 
               default:
               w->color(59);
               } 
      
      }
    else
      {     
           switch(t)
             {
               case 5:
               w->color(133);
               break;
               case 6:
               w->color(1);
               break;
               default:    
               w->color(2);
             }  
      }         
   
    w->redraw(); 
  }
  
  rmgmo->cocas[i]=0;
  rmgmo->lastvelo[i]=0;
  
  }
}

void stygmorgan::FunciLeds(int ifun) {
  if (ifun==0)
{
  AcutaLeds(ifun);
  return;
}

if (ifun==1)
{  
  for (int t=9; t<=15; t++)
    {
       rmgmo->lastvelo[t]=0;
       rmgmo->cocas[t]=1;
  
     }  
     
    AcutaLeds(ifun); 
 }
}

